mapper.select:
SELECT T1.*,T2.* FROM ( SELECT B.B_SEQ as bSeq, B.B_TITLE as bTitle, B.B_CONTENTS as bContents , B.ID as Id, B.READ_CNT as readCnt, CASE TO_CHAR(B.reg_dt, 'yyyy/mm/dd') when TO_CHAR(sysdate, 'yyyy/mm/dd') THEN TO_CHAR(sysdate, 'HH24:MI') ELSE TO_CHAR(B.reg_dt, 'yyyy/mm/dd') END AS regDt, B.udt_user as udtUser, CASE TO_CHAR(B.udt_dt, 'yyyy/mm/dd') when TO_CHAR(sysdate, 'yyyy/mm/dd') THEN TO_CHAR(sysdate, 'HH24:MI') ELSE TO_CHAR(B.udt_dt, 'yyyy/mm/dd') END AS udtDt, B.B_CODE as bCode, B.P_CODE as pCode, B.rnum as num FROM ( SELECT ROWNUM AS rnum, A.* FROM ( SELECT * FROM board WHERE 1=1 AND reg_dt > to_date('19700101','yyyymmdd')
ORDER BY bSeq DESC )A WHERE rownum <= (#{pageSize} * (#{pageNum}-1)+#{pageSize}) ) B WHERE B.rnum >= (#{pageSize} * (#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM board WHERE 1=1
)T2

mapper.update:
UPDATE BOARD
SET  READ_CNT = (SELECT NVL(MAX(READ_CNT),0)+1 FROM BOARD WHERE b_seq = #{bSeq})
WHERE b_seq = #{bSeq}

UPDATE board
SET
    b_seq = #{bSeq}
    ,b_title = #{bTitle,jdbcType=VARCHAR}
    , b_contents = #{bContents,jdbcType=VARCHAR}
    , id = #{Id,jdbcType=VARCHAR}
    , read_cnt = 0
    , reg_dt = SYSDATE
    , udt_user = #{udtUser,jdbcType=VARCHAR}
    , udt_dt = SYSDATE
    , b_code = #{bCode}
    , p_code = #{pCode}
WHERE b_seq= #{bSeq}
