mapper.select:
SELECT T1.*,T2.* FROM ( SELECT * FROM ( SELECT ROWNUM AS rnum, A.* FROM ( SELECT U.NICKNAME as nickname, U.ID as u_id, O1.ORDER_CODE as order_code, O3.DETAIL_CODE as detail_code, P.P_PRICE as p_price, O2.UNIT_PRICE as unit_price, P.P_IMAGE as p_image, P.P_NAME as p_name, P.P_CONTENT as p_content, O2.QUANTITY as quantitiy, O3.UDT_DT as udt_dt, O3.REG_DT as reg_dt, DECODE(O3.OD_STATUS,0,'주문완료',1,'결제대기',2,'배송준비',3,'배송중',4,'배송완료',5,'환불접수',6,'환불완료',7,'교환신청접수',9,'취소') as od_status FROM USERS U JOIN ORDERS O1 ON U.ID = O1.ID JOIN ORDER_DETAIL O2 ON O1.ORDER_CODE = O2.ORDER_CODE JOIN ORDER_STATUS O3 ON O3.DETAIL_CODE = O2.DETAIL_CODE JOIN PRODUCT P ON O2.P_CODE = P.P_CODE AND udt_dt > to_date('19700101','yyyymmdd') WHERE 1=1
ORDER BY detail_code DESC )A WHERE ROWNUM <= (#{pageSize} * (#{pageNum}-1)+#{pageSize}) ) B WHERE B.rnum >= (#{pageSize} * (#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM order_Status WHERE 1=1
)T2
