mapper.select:
SELECT T1.*,T2.* FROM (SELECT rnum as num, B.p_code as pCode, B.h_code as hCode, B.l_code as lCode, B.p_name as pName, B.p_company as pCompany, B.p_price as pPrice, B.p_image as pImage, B.stock as stock, B.unit_sales as unitsales, B.status as status, B.p_new as pNew, B.p_best as pBest, B.p_sale as pSale, B.p_content as pContent, B.sale_percent as salePercent, TO_CHAR(B.reg_dt, 'yyyy/mm/dd') reg_dt FROM (SELECT ROWNUM as rnum, A.* FROM (SELECT * FROM PRODUCT WHERE 1=1
ORDER BY reg_dt DESC )A WHERE <= (#{pageSize} * (#{pageNum}-1)+#{pageSize}) )B WHERE b.rnum >= (#{pageSize} * (#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM PRODUCT WHERE 1=1
)T2
