mapper.select:
SELECT T1.*,T2.* FROM (SELECT rnum , B.p_code , B.h_code , B.l_code , B.p_name , B.p_company , B.p_price , B.p_image , B.stock , B.unit_sales , B.status , B.p_new , B.p_best , B.p_sale , B.p_content , B.sale_percent , TO_CHAR(B.reg_dt, 'yyyy/mm/dd') FROM (SELECT ROWNUM as rnum, A.* FROM (SELECT * FROM PRODUCT WHERE 1=1
ORDER BY reg_dt DESC )A WHERE rownum <= (#{pageSize} * (#{pageNum}-1)+#{pageSize}) )B WHERE b.rnum >= (#{pageSize} * (#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM PRODUCT WHERE 1=1
)T2
